SELECT

FROM
`mall_sub_order`


        status = #{status}


        and stock_status = #{stockStatus}


        and order_no LIKE concat('%',#{orderNo},'%')


        and upc = #{upc}


        and sku_code = #{skuCode}


        and open_id = #{openId}


        and item_name LIKE concat('%',#{itemName},'%')


        and receiver LIKE concat('%',#{receiver},'%')


        and telephone = #{telephone}


        and warehouse_no = #{warehouseNo}


        and logistic_type = #{logisticType}


        and close_reason = #{closeReason}



        and    gmt_create >= #{startGmtCreate} 


        and     gmt_create <= #{endGmtCreate} 


        and company_no = #{companyNo}

SELECT T1.id, T1.order_no, T1.mall_return_order_no, T1.customer_no, T1.open_id, T1.company_no, T1.shop_code, T1.channel_order_no, T1.order_time, T1.item_code, T1.item_name, T1.sku_code, T1.channel_sku_code, T1.upc, T1.scale, T1.sku_pic, T1.logistic_type, T1.freight, T1.freight_real, T1.weight, T1.sale_price, T1.sale_price_agent, T1.quantity, T1.status, T1.close_reason, T1.warehouse_no, T1.stock_status, T1.shipping_order_no, T1.shipping_no, T1.receiver, T1.receiver_country, T1.receiver_state, T1.receiver_city, T1.receiver_district, T1.receiver_address, T1.telephone, postcode, T1.id_card, T1.idcard_pic_front, T1.idcard_pic_reverse, T1.memo, T1.gmt_create, T1.gmt_modify, T1.creator, T1.modifier, T1.is_del , T2.nick_name AS nickName FROM mall_sub_order T1 LEFT JOIN mall_customer T2 ON T1.open_id = T2.open_id
T1.status = #{status}
and T1.stock_status = #{stockStatus}
and T1.order_no LIKE concat('%',#{orderNo},'%')
and T1.shop_code LIKE concat('%',#{shopCode},'%')
and T1.upc = #{upc}
and T1.open_id = #{openId}
and T1.sku_code = #{skuCode}
and T1.item_name LIKE concat('%',#{itemName},'%')
and T1.receiver = #{receiver}
and T1.telephone = #{telephone}
and T1.warehouse_no = #{warehouseId}
and T1.logistic_type = #{logisticType}
and T1.order_time >= #{startGmtCreate}
and T1.order_time <= #{endGmtCreate}
order by T1.id desc
