mapper.select:
SELECT DISTINCT s.id AS id, s. NAME AS 'name', s.url AS url, s.description AS description, s.icon AS icon, s.pid AS pid, s.seq AS seq, s. STATUS AS 'status', s.resource_type AS resource_type, s.create_time AS create_time FROM role r LEFT JOIN role_resource e ON r.id = e.role_id LEFT JOIN resource s ON e.resource_id = s.id WHERE r.id in
#{item}
AND s.resource_type = 0 ORDER BY seq

SELECT DISTINCT s.id AS id, s. NAME AS 'name', s.url AS url, s.description AS description, s.icon AS icon, s.pid AS pid, s.seq AS seq, s. STATUS AS 'status', s.resource_type AS resource_type, s.create_time AS create_time FROM role r LEFT JOIN role_resource e ON r.id = e.role_id LEFT JOIN resource s ON e.resource_id = s.id WHERE r.id in
#{item}
AND s.url IS NOT NULL ORDER BY seq
