mapper.update:
UPDATE
commission_sumary_detail
SET status=#{status},
    settlement_no=#{settlementNo}
where id = #{id}

UPDATE
commission_sumary_detail
SET status=#{status}
where share_user_id = #{userId}
and is_del is false

mapper.select:
SELECT IFNULL(sum(IFNULL(settlement,0)),0)
FROM commission_sumary_detail
WHERE share_user_id = #{userId}

SELECT IFNULL(sum(IFNULL(settlement,0)),0)
FROM commission_sumary_detail
WHERE share_user_id = #{userNo}
AND status=#{status}

SELECT sub_order_no
FROM commission_sumary_detail
WHERE share_user_id = #{userNo}
 LIMIT #{firstStart},#{pageSize}
