mapper.select:
   SELECT
   	count(1)
   FROM
       item_sku T1
   where
		   1=1

           and T1.item_name LIKE concat('%',#{itemName},'%')


          and T1.item_code = #{itemCode}


          and T1.upc = #{upc}


          and T1.sku_code = #{skuCode}


         and  T1.en_name LIKE concat('%',#{enName},'%')


         and  T1.category_id = #{categoryId}


         and  T1.brand = #{brand}


           and T1.color LIKE concat('%',#{color},'%')


           and T1.buy_site LIKE concat('%',#{buySite},'%')


        and    T1.gmt_create >= #{startGmt} 


       and     T1.gmt_create <= #{endGmt} 


       and     T1.company_id = #{companyId}

        SELECT
        	 T1.id as id,T1.sale_type AS saleType,T1.brand as brand,T1.weight as weight,T1.sku_pic as skuPic,T1.item_id AS itemId,T1.item_name AS itemName,T1.category_id AS categoryId,T1.category_name AS categoryName,
			 T1.item_code AS itemCode,T1.sku_code AS skuCode,T1.upc as upc,T1.salable as salable,T1.color as color,T1.scale as scale,T1.purchase_price AS purchasePrice,T1.freight AS freight,T1.sale_price AS salePrice,T1.remark as remark,T1.gmt_create AS gmtCreate,T1.gmt_modify AS gmtModify,T1.user_create AS userCreate,T1.user_modify AS userModify,
			 T1.package_id as packageId,T1.package_level_id as packageLevelId,T1.package_name AS packageName,T1.package_en as packageEn,T1.package_weight as packageWeight,T1.buy_site as buySite,inv.locked_inv as lockedInv,inv.inventory as inventory
			 ,inv.virtual_inv as virtualInv,inv.locked_virtual_inv lockedVirtualInv,inv.trans_inv as transInv,inv.locked_trans_inv as lockedTransInv,(inv.inventory-inv.locked_inv+inv.trans_inv-inv.locked_trans_inv) as totalAvailableInv ,T1.third_sku_code AS thirdSkuCode

                ,getTaskDailyItemSkuCase(T1.id) as taskDailyItemSkuCase 

        FROM
            item_sku T1 left join inventory inv on T1.id= inv.sku_id 
        where
           1=1

                and T1.item_name LIKE concat('%',#{itemName},'%')


               and T1.item_code = #{itemCode}


               and T1.upc = #{upc}


               and T1.sku_code = #{skuCode}


              and  T1.en_name LIKE concat('%',#{enName},'%')


              and  T1.category_id = #{categoryId}


              and  T1.brand = #{brand}


                and T1.color LIKE concat('%',#{color},'%')


                and T1.buy_site LIKE concat('%',#{buySite},'%')


             and    T1.gmt_create >= #{startGmt} 


            and     T1.gmt_create <= #{endGmt} 


            and     T1.company_id = #{companyId} 

          order by T1.id desc
          	 LIMIT #{firstStart},#{pageSize}

SELECT T1.id as id,T1.sale_type AS saleType,T1.brand as brand,T1.weight as weight,T1.sku_pic as skuPic,T1.item_id AS itemId,T1.item_name AS itemName,T1.category_id AS categoryId,T1.category_name AS categoryName, T1.item_code AS itemCode,T1.sku_code AS skuCode,T1.upc as upc,T1.salable as salable,T1.color as color,T1.scale as scale,T1.purchase_price AS purchasePrice,T1.freight AS freight,T1.sale_price AS salePrice,T1.remark as remark,T1.gmt_create AS gmtCreate,T1.gmt_modify AS gmtModify,T1.user_create AS userCreate,T1.user_modify AS userModify, T1.package_id as packageId,T1.package_level_id as packageLevelId,T1.package_name AS packageName,T1.package_en as packageEn,T1.package_weight as packageWeight,inv.locked_inv as lockedInv,inv.inventory as inventory ,inv.virtual_inv as virtualInv,inv.locked_virtual_inv lockedVirtualInv,inv.trans_inv as transInv,inv.locked_trans_inv as lockedTransInv,(inv.inventory-inv.locked_inv+inv.trans_inv-inv.locked_trans_inv) as totalAvailableInv,T1.cost_price AS costPrice,T1.discount AS discount,T1.third_sku_code AS thirdSkuCode FROM item_sku T1 left join inventory inv on T1.id= inv.sku_id where 1=1
and T1.item_id = #{itemId}
and T1.id = #{skuId}
