SELECT T1.sku_code as skuCode,T1.item_name as itemName,T3.sku_pic as skuPic,T1.color,T1.scale,T1.sale_price AS salePrice,T3.brand,T3.upc,T1.quantity,T2.gmt_create as gmtCreate,T1.sales_name as salesName,T1.erp_no as erpNo,T1.receiver,T1.telephone, T1.receiver_state as receiverState,T1.receiver_city as receiverCity,T1.receiver_district as receiverDistrict,T1.address_detail as addressDetail,T4.name as warehouseName,T2.shipping_no as shippingNo,T3.logistic_type as logisticType,T2.logistic_no as logisticNo,T2.logistic_company as logisticCompany,T5.nick_name AS nickName,T2.type As type FROM erp_order T1 LEFT JOIN shipping_order T2 ON T1.shipping_order_id=T2.id LEFT JOIN item_sku T3 ON T1.sku_id=T3.id LEFT JOIN warehouse T4 ON T1.warehouse_id=T4.id LEFT JOIN wx_user T5 ON T1.open_id = T5.open_id WHERE T1.status=2
